import React,{useState,setState} from 'react';
import ReactPlayer from 'react-player'
import './Upload.css';
// import './Upload.scss';
// import Image from './logo.svg';
import axios from 'axios';
// import {CopyToClipboard } from 'react-copy-to-clipboard';
// import {MdDone} from 'react-icons/md';
import Dropzone from 'react-dropzone';

function sendtoserver(files){

}

const Upload=()=>{
    // const [image,setImage]=useState(null);
    const [progress,setProgress]=useState(null);
    const [controls,setControls]=useState(true);
    const [currentlyUpload,setCurrentlyUpload]=useState(false);
    const [error,setError]=useState({
        found:false,
        message: ''
    });
    const [image,setImage]=useState('');
    const [video,setVideo]=useState('');

    // const [copied,setCopied]=useState(false);
    const maxSize=5000000 ;
    // const onCopyText=()=>{
    //     setCopied(true)
    //     setTimeout(() => {
    //         setCopied(false)
    //     }, 1000);
    // }
    const onDrop=(acceptedFiles)=>{
        sendtoserver(acceptedFiles)
    }

    const handleOnSubmit=  ({target:{files}})=>{
                let formData=new FormData();
                formData.append('file',files[0]);
                const options={
                    // onUploadProgress: (progressEvent)=>{
                    //    const {loaded,total} = progressEvent;
                    //    let percent =Math.floor((loaded*100)/total);
                    //    console.log(`${loaded}kb of ${total} kb | ${percent} % `);
                    //    if(percent>0 && percent<100){
                    //         setProgress(percent);
                    //         setCurrentlyUpload(true);
                    //    }

                    //   }

                }

                axios.post('http://127.0.0.1:5000/api',formData,options).then(res=>
                { 
                        console.log(res)
                        setTimeout(()=>{
                            console.log(res.data.img);
                            if(res.data.img != undefined){
                                setImage('http://127.0.0.1:5000/img/'+res.data.img);
                            }
                            if(res.data.vid != undefined){
                                setVideo('http://127.0.0.1:5000/img/'+res.data.vid);
                            }

                            setProgress(0);
                            setCurrentlyUpload(false);
                        },2000)
                }
                ).catch((err)=>{
                    console.log(err);
                    if(err.response.status === 400){
                        const errMsg=err.response.data;
                        console.log(err.response.data.errors)
                        setError({
                            found:true,
                            message:err.response.data.errors
                        })
                        if(errMsg){ 
                            console.log(errMsg)
                        }
                    }else{
                        console.log('Another error %s',err)
                    }
                });

    }
    return(
        <>
        {
            (

                <div className="container">
                {
                    (
                    <>
                        <h3>Upload Image or Video</h3>
                        {/* <p>File should be Jpeg,Png,..</p> */}
                        <Dropzone onDrop={onDrop} multiple={false} minSize={0} maxSize={maxSize}>
                            {
                                ({ getRootProps,getInputProps })=>(
                                    <div {...getRootProps({ className: "file-container" })} onChange={handleOnSubmit}>
                                        <input {...getInputProps()} />
                                        {/* <img src={Image} alt="example"/> */}
                                        <p>Drop Image or Video Here </p>
                                        <input type="file"  onChange={handleOnSubmit} name="upfile"/>
                                    </div>
                                )
                            }

                        </Dropzone>
                        {/* <button type="submit"  className="btn-upload">Upload file</button> */}

                     </>  
                    )
                }

             </div>
            )
        }       
        <div className="content">
            <h4>Result:</h4>
            <img src={image}/>
            <ReactPlayer url={video} controls={controls} width='100%' height='100%'/>
        </div>
         </>
    )
};

export default Upload;
